export function Articles({ search }: { search: string }) {
  const [selectedArticle, setSelectedArticle] = useState<Article | null>(null);

  // Filtriamo gli articoli in base alla ricerca
  const filteredArticles = articles.filter((article) =>
    article.title.toLowerCase().includes(search.toLowerCase())
  );

  const handleReadMore = (article: Article) => {
    setSelectedArticle(article); // Mostra l'articolo completo
  };

  if (selectedArticle) {
    return (
      <div className="full-article">
        <h2>{selectedArticle.title}</h2>
        <p>{selectedArticle.fullContent}</p>
        <Button onClick={() => setSelectedArticle(null)}>Torna alla lista</Button>
        <Button><a href={selectedArticle.site}>Visita il sito </a></Button>
      </div>
    );
  }

  return (
    <main className="p-6 grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
      {filteredArticles.length === 0 ? (
        <p>Nessun articolo trovato.</p>
      ) : (
        filteredArticles.map((item) => (
          <Card key={item.id} className="shadow-lg bg-white p-4 rounded-lg">
            <CardContent className='card'>
              <h3 className="text-xl font-semibold">{item.title}</h3>
              <CardMedia
                component="img"
                height="237"
                image={item.image}
              />
              <p className="text-gray-600 mt-2">{item.description}</p>
              <Button className="mt-4" onClick={() => handleReadMore(item)}>
                Leggi di pi√π
              </Button>
            </CardContent>
          </Card>
        ))
      )}
    </main>
  );
}
